package Looping;

public class Forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int [] num = {10,20,30,40,50};
     
     for(int x: num) {
     System.out.print(x );
     System.out.print("\n");
     }
     
     String [] names = {"joshua","jb"};
     for(String name: names) {
         System.out.print( name);
         }
     System.out.println();
}
}
